      out.write("\t\t\t\t$('.bannerdot img').eq(i).addClass('activeDot');\r\n");
      out.write("\r\n");
      out.write("\t\t\t} else {\r\n");
      out.write("\t\t\t\t$(\".banner li\").eq(i).fadeOut(2000).siblings(\"li\").eq(0).fadeIn(2000);\r\n");
      out.write("\t\t\t\ti = 0;\r\n");
      out.write("\t\t\t\t$('.bannerdot img').attr('src', paths+'/statics/themes/default/images/bannerdot.png');\r\n");
